import React, {useEffect, useState} from 'react';
import { Container, Grid, Card, CardContent, CardMedia, Typography, Button, CircularProgress } from '@mui/material';
import { makeStyles } from '@mui/styles'; 

const useStyles = makeStyles({
  root: {
    padding: '20px',  
  }, 
  card: { 
    maxWidth: 345, 
    margin: 'auto', 
    position: 'relative', 
  }, 
  media: {
    height: 140, 
  }, 
  deleteButton: {
    position: 'absolute', 
    top: 10, 
    right: 10, 
  },  
}); 

const ProductList = () => {

  const classes = useStyles();  
  const [products, setProducts] = useState([]); 
  const [loading, setLoading] = useState(true); 
  const [error, setError] = useState(null); 

  //implement the get products function
  const fetchProducts = () => {
    setLoading(true);
    fetch('http://localhost:5000/api/products')
      .then(response => response.json())
      .then(data => {
        setProducts(data); 
        setLoading(false); 
      })
      .catch(error => {
        console.error('Error fetching a list of products:', error); 
        setError('Error fetching products'); 
        setLoading(false);      
      }); 
  };

  useEffect(() => {
    fetchProducts(); 
  }, []); 

  //implement the delete function
  const handleDelete = (id) => {
    const updatedProducts = products.filter(product => product.id !== id);
    setProducts(updatedProducts);
  };

  return (
    <Container className={classes.root}>

    <Grid container spacing={3}> 
      {products.map(product => (
        <Grid item xs={12} sm={6} md={4} key={product.id}> 
          <Card> className={classes.card}> 
            <CardMedia
              className={classes.media}
              image={product.imageUrl || 'https://via.placeholder.com/150'}
              title={product.name}
            />
            <CardContent> 
              <Typography gutterButtom variant="h5" component="div"> 
                {product.name} 
              </Typography> 
              <Typography variant="body2" color="text.secondary"> 
                {product.description}
              </Typography> 
            </CardContent> 
          </Card> 
        </Grid> 
      ))}
      </Grid> 

    </Container>

  );
};

export default ProductList;
